*{
  margin: 0;
  padding: 0;
}

.container {
  width: 100%;
  height: 100vh;
  background: url(../images/cham.svg) center no-repeat;
  background-size: cover;
}

.hero-text {
  color:#fff;
  width: 60%;
  position: absolute;
  font-family: 'Roboto Condensed', sans-serif;
  top: 4%;
  left: 10%;
  display: flex;
  flex-direction: column;
  letter-spacing: 1.5;
  font-size: 20px;
  line-height: 1.9;
}

.hero-heading{
padding-bottom: 20px;
font-family: 'Oswald', sans-serif;
font-size: 70px;
font-weight: bold;
}

.banner {
  color: #fff;
  width: 200px;
  position: absolute;
  top: 55%;
  left: 9%;
  display: flex;
  flex-direction: column;
  justify-content:right;
  align-items:center;
  letter-spacing: .5;
}

.arrow {
  font-size: 30px;
  margin-bottom: 15px;
  animation: anim 3s infinite;
}

.banner-btn {
width: 150px;
padding: 10px;
font-family: 'Roboto Condensed', sans-serif;
font-size: 17px;
text-transform: uppercase;
letter-spacing: 1px;
background: transparent;
color: #fff;
border-radius: 50px;
border: 2px solid #fff;
outline:none;
cursor: pointer;
transition:.3s;
}

.banner-btn:hover{
  transform: translateY(-5px);
  color:#4A90E2;
  border: 2px solid #4A90E2;
}

@keyframes anim {
  0% {transform: translateY(0)}
  20% {transform: translateY(0)}
  40% {transform: translateY(-20px)}
  50% {transform: translateY(0);}
  60% {transform: translateY(-10px);}
  80% {transform: translateY(0);}
  100% {transform: translateY(0);}
}
/*adding more from prev project*/
.form-container {
  display: flex;
  width: 800px;
  height: 95vh;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  box-shadow: 0 20px 50px rgba(0, 0, 0, .4);
  opacity: 0;
  visibility: hidden;
  transition: opacity 1s;
}


.sign-up-form {
  width: 100%;
  padding: 120px 50px;
  box-sizing: border-box;
  background: rgba( 74, 144, 226, 0.25 );
  box-shadow: 0 8px 32px 0 rgba( 31, 38, 135, 0.37 );
  backdrop-filter: blur( 8.0px );
  -webkit-backdrop-filter: blur( 8.0px );
  border-radius: 10px;
  border: 1px solid rgba( 255, 255, 255, 0.18 );
}


.form-input-heading {
  
  font-family: 'Oswald', sans-serif;
  font-size: 50px;
  letter-spacing: 1px;
  color: #fff;
  background-color: transparent;
  border: none;
  margin-bottom: 50px;
  outline: none;
  transition: border-bottom-color .8s;
}

.form-input-heading :focus {
  border-bottom-color: #4A90E2;
}

.form-input {
  width: 300px;
  font-family: 'Oswald', sans-serif;
  font-size: 18px;
  letter-spacing: 1px;
  color: #fff;
  background-color: transparent;
  border: none;
  border-bottom: 2px solid #fff;
  margin-bottom: 50px;
  outline: none;
  padding: 10px 0;
  transition: border-bottom-color .8s;
}

.form-input:focus {
  border-bottom-color: #4A90E2;
}

.sign-up-form p {
  color: #fff;
  font-family: 'Oswald', sans-serif;
  font-size: 18px;
  letter-spacing: 1px;
  font-weight: 200;
  margin-bottom: 8px;
}

.sign-up-form label {
  color: #fff;
  font-family: 'Oswald', sans-serif;
  font-size: 15px;
  font-weight: 200;
  letter-spacing: 1px;
  text-transform: uppercase;
}

.sign-up-form input[type="checkbox"] {
  position: relative;
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
  width: 30px;
  height: 15px;
  background-color: #fff;
  outline: none;
  border-radius: 20px;
  margin: 0 8px;
  box-shadow: inset 0 0 10px rgba(0, 0, 0, .4);
  transition: background-color .5s;
}

.sign-up-form input:checked[type="checkbox"] {
  background-color: #4A90E2;
}

.sign-up-form input[type="checkbox"]::before {
  content: '';
  width: 17px;
  height: 17px;
  background-color: #fff;
  position: absolute;
  border-radius: 50%;
  top: -1px;
  left: -4px;
  box-shadow: 0 2px 5px rgba(0, 0, 0, .4);
  transition: left .5s;
}

.sign-up-form input:checked[type="checkbox"]::before {
  left: 18px;
}

.sign-up-form button {
  display: block;
  margin-top: 80px;
  width: 150px;
  padding: 8px 5px;
  background-color: transparent;
  color: #fff;
  border: 2px solid #fff;
  border-radius: 50px;
  outline: none;
  font-family: 'Roboto Condensed', sans-serif;
  font-size: 15px;
  letter-spacing: 1px;
  text-transform: uppercase;
  cursor: pointer;
}

.header {
   width: 50%;
   background: linear-gradient(to top, rgba(0, 0, 0, .4), rgba(0, 0, 0, .2));
   background-size: cover;
   box-shadow: -10px 0 30px rgba(0, 0, 0, .3);
   position: relative;
}

.header h1 {
   position: absolute;
   top: 15%;
   left: 50%;
   transform: translateX(-50%);
   text-align: center;
   color: #fff;
   font-family: 'Oswald', sans-serif;
   font-size: 40px;
   text-transform: uppercase;
   letter-spacing: 1px;
}

.x-btn {
  position: absolute;
  top: -3%;
  right: -3%;
  font-size: 25px;
  font-weight: bold;
  color: #fff;
  cursor: pointer;
}

